pragma solidity ^0.8.20;
import { ERC20 } from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

library Erc20Helper {
  /**
   * @dev Checks if a token is an ERC-20
   * @param token Address of the token
   * @return bool True if the token is an ERC-20, false otherwise
   */
  function isERC20(address token) internal view returns (bool) {
    try ERC20(token).totalSupply() returns (uint256) {
      try ERC20(token).balanceOf(address(this)) returns (uint256) {
        return true;
      } catch {
        return false;
      }
    } catch {
      return false;
    }
  }
}

