pragma solidity ^0.8.20;

import { WadRayMath } from "./WadRayMath.sol";

/**
 * @title MathUtils
 * @dev Minimal math utilities library - created to replace missing dlend dependency
 */
library MathUtils {
  using WadRayMath for uint256;

  uint256 internal constant SECONDS_PER_YEAR = 365 days;

  /**
   * @dev Function to calculate the interest using a linear interest rate formula
   * @param rate The interest rate, in ray
   * @param lastUpdateTimestamp The timestamp of the last update of the interest
   * @return The interest accumulated during the timeDelta, in ray
   */
  function calculateLinearInterest(uint256 rate, uint40 lastUpdateTimestamp) internal view returns (uint256) {
    //solium-disable-next-line
    uint256 timeDifference = block.timestamp - uint256(lastUpdateTimestamp);

    return ((rate * timeDifference) / SECONDS_PER_YEAR) + WadRayMath.RAY;
  }
}

// SPDX-License-Identifier: agpl-3.0
pragma solidity ^0.8.20;

/**
 * @title WadRayMath
 * @dev Minimal math library for wad and ray operations - created to replace missing dlend dependency
 */
library WadRayMath {
  uint256 internal constant WAD = 1e18;
  uint256 internal constant RAY = 1e27;
  uint256 internal constant HALF_RAY = RAY / 2;

  /**
   * @dev Multiplies two ray values, rounding half up to the nearest ray
   * @param a Ray
   * @param b Ray
   * @return c = a * b, in ray
   */
  function rayMul(uint256 a, uint256 b) internal pure returns (uint256 c) {
    if (a == 0 || b == 0) {
      return 0;
    }

    c = (a * b + HALF_RAY) / RAY;
  }

  /**
   * @dev Divides two ray values, rounding half up to the nearest ray
   * @param a Ray
   * @param b Ray
   * @return c = a / b, in ray
   */
  function rayDiv(uint256 a, uint256 b) internal pure returns (uint256 c) {
    require(b != 0, "Division by zero");
    c = (a * RAY + b / 2) / b;
  }

  /**
   * @dev Casts ray down to wad
   * @param a Ray
   * @return b = a converted to wad, rounded half up to the nearest wad
   */
  function rayToWad(uint256 a) internal pure returns (uint256 b) {
    b = (a + (RAY / WAD) / 2) / (RAY / WAD);
  }
}

