// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IPool } from "../../interfaces/dlend/core/interfaces/IPool.sol";
import { DataTypes } from "../../interfaces/dlend/core/protocol/libraries/types/DataTypes.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { StaticATokenLM } from "./StaticATokenLM.sol";
import { IStaticATokenFactory } from "./interfaces/IStaticATokenFactory.sol";
import { IRewardsController } from "../../interfaces/dlend/periphery/rewards/IRewardsController.sol";

/**
 * @title StaticATokenFactory
 * @notice Factory contract that keeps track of all deployed static aToken wrappers for a specified pool.
 * This registry also acts as a factory, allowing to deploy new static aTokens on demand.
 * There can only be one static aToken per underlying on the registry at a time.
 * @author BGD labs (modified by dTrinity)
 */
contract StaticATokenFactory is IStaticATokenFactory {
  IPool public immutable POOL;

  mapping(address => address) internal _underlyingToStaticAToken;
  address[] internal _staticATokens;

  event StaticTokenCreated(address indexed staticAToken, address indexed underlying);

  constructor(IPool pool) {
    POOL = pool;
  }

  function initialize() external pure {
    revert("NO_INITIALIZER");
  }

  ///@inheritdoc IStaticATokenFactory
  function createStaticATokens(address[] memory underlyings) external returns (address[] memory) {
    address[] memory staticATokens = new address[](underlyings.length);
    for (uint256 i = 0; i < underlyings.length; i++) {
      address cachedStaticAToken = _underlyingToStaticAToken[underlyings[i]];
      if (cachedStaticAToken == address(0)) {
        DataTypes.ReserveData memory reserveData = POOL.getReserveData(underlyings[i]);
        require(reserveData.aTokenAddress != address(0), "UNDERLYING_NOT_LISTED");
        StaticATokenLM staticAToken = new StaticATokenLM(
          POOL,
          IRewardsController(address(0)), // TODO: pass correct incentives controller if needed
          reserveData.aTokenAddress,
          string(abi.encodePacked("Wrapped ", IERC20Metadata(reserveData.aTokenAddress).name())),
          string(abi.encodePacked("w", IERC20Metadata(reserveData.aTokenAddress).symbol()))
        );
        address staticATokenAddr = address(staticAToken);
        _underlyingToStaticAToken[underlyings[i]] = staticATokenAddr;
        staticATokens[i] = staticATokenAddr;
        _staticATokens.push(staticATokenAddr);
        emit StaticTokenCreated(staticATokenAddr, underlyings[i]);
      } else {
        staticATokens[i] = cachedStaticAToken;
      }
    }
    return staticATokens;
  }

  ///@inheritdoc IStaticATokenFactory
  function getStaticATokens() external view returns (address[] memory) {
    return _staticATokens;
  }

  ///@inheritdoc IStaticATokenFactory
  function getStaticAToken(address underlying) external view returns (address) {
    return _underlyingToStaticAToken[underlying];
  }
}

// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

// --- DLend fork imports ---
import { IPool } from "../../interfaces/dlend/core/interfaces/IPool.sol";
import { DataTypes } from "../../interfaces/dlend/core/protocol/libraries/types/DataTypes.sol";
import { ReserveConfiguration } from "../../interfaces/dlend/core/protocol/libraries/configuration/ReserveConfiguration.sol";
import { IScaledBalanceToken } from "../../interfaces/dlend/core/interfaces/IScaledBalanceToken.sol";
import { IRewardsController } from "../../interfaces/dlend/periphery/rewards/IRewardsController.sol";
import { WadRayMath } from "../../interfaces/dlend/core/protocol/libraries/math/WadRayMath.sol";
import { MathUtils } from "../../interfaces/dlend/core/protocol/libraries/math/MathUtils.sol";
import { SafeCast } from "../../interfaces/dlend/core/dependencies/openzeppelin/contracts/SafeCast.sol";
import { Initializable } from "../../interfaces/dlend/core/dependencies/openzeppelin/upgradeability/Initializable.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IERC20Metadata } from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { IERC20WithPermit } from "../../interfaces/dlend/core/interfaces/IERC20WithPermit.sol";
// --- Local imports ---
import { IStaticATokenLM } from "./interfaces/IStaticATokenLM.sol";
import { IAToken } from "./interfaces/IAToken.sol";
import { ERC20 } from "./ERC20.sol";
import { StaticATokenErrors } from "./StaticATokenErrors.sol";
import { RayMathExplicitRounding, Rounding } from "./RayMathExplicitRounding.sol";
import { IERC4626 } from "./interfaces/IERC4626.sol";
import { ECDSA } from "./ECDSA.sol";

/**
 * @title StaticATokenLM
 * @notice Wrapper smart contract that allows to deposit tokens on the Aave protocol and receive
 * a token which balance doesn't increase automatically, but uses an ever-increasing exchange rate.
 * It supports claiming liquidity mining rewards from the Aave system.
 * @author BGD labs
 */
contract StaticATokenLM is ERC20, IStaticATokenLM, IERC4626 {
  using SafeERC20 for IERC20;
  using SafeCast for uint256;
  using WadRayMath for uint256;
  using RayMathExplicitRounding for uint256;

  bytes32 public constant METADEPOSIT_TYPEHASH =
    keccak256(
      "Deposit(address depositor,address receiver,uint256 assets,uint16 referralCode,bool depositToAave,uint256 nonce,uint256 deadline,PermitParams permit)"
    );
  bytes32 public constant METAWITHDRAWAL_TYPEHASH =
    keccak256(
      "Withdraw(address owner,address receiver,uint256 shares,uint256 assets,bool withdrawFromAave,uint256 nonce,uint256 deadline)"
    );

  uint256 public constant STATIC__ATOKEN_LM_REVISION = 2;

  IPool public immutable POOL;
  IRewardsController public immutable REWARDS_CONTROLLER;

  IERC20 internal _aToken;
  address internal _aTokenUnderlying;
  address[] internal _rewardTokens;
  mapping(address => RewardIndexCache) internal _startIndex;
  mapping(address => mapping(address => UserRewardsData)) internal _userRewardsData;

  constructor(
    IPool pool,
    IRewardsController rewardsController,
    address newAToken,
    string memory staticATokenName,
    string memory staticATokenSymbol
  ) ERC20(staticATokenName, staticATokenSymbol, IERC20Metadata(newAToken).decimals()) {
    POOL = pool;
    REWARDS_CONTROLLER = rewardsController;
    _aToken = IERC20(newAToken);
    _aTokenUnderlying = IAToken(newAToken).UNDERLYING_ASSET_ADDRESS();
    IERC20(_aTokenUnderlying).forceApprove(address(POOL), type(uint256).max);
    if (address(REWARDS_CONTROLLER) != address(0)) {
      refreshRewardTokens();
    }
  }

  ///@inheritdoc IStaticATokenLM
  function refreshRewardTokens() public override {
    address[] memory rewards = REWARDS_CONTROLLER.getRewardsByAsset(address(_aToken));
    for (uint256 i = 0; i < rewards.length; i++) {
      _registerRewardToken(rewards[i]);
    }
  }

  ///@inheritdoc IStaticATokenLM
  function isRegisteredRewardToken(address reward) public view override returns (bool) {
    return _startIndex[reward].isRegistered;
  }

  ///@inheritdoc IStaticATokenLM
  function metaDeposit(
    address depositor,
    address receiver,
    uint256 assets,
    uint16 referralCode,
    bool depositToAave,
    uint256 deadline,
    PermitParams calldata permit,
    SignatureParams calldata sigParams
  ) external returns (uint256) {
    require(depositor != address(0), StaticATokenErrors.INVALID_DEPOSITOR);
    //solium-disable-next-line
    require(deadline >= block.timestamp, StaticATokenErrors.INVALID_EXPIRATION);
    uint256 nonce = nonces[depositor];

    // Unchecked because the only math done is incrementing
    // the owner's nonce which cannot realistically overflow.
    unchecked {
      bytes32 digest = keccak256(
        abi.encodePacked(
          "\x19\x01",
          DOMAIN_SEPARATOR(),
          keccak256(abi.encode(METADEPOSIT_TYPEHASH, depositor, receiver, assets, referralCode, depositToAave, nonce, deadline, permit))
        )
      );
      nonces[depositor] = nonce + 1;
      require(depositor == ECDSA.recover(digest, sigParams.v, sigParams.r, sigParams.s), StaticATokenErrors.INVALID_SIGNATURE);
    }
    // assume if deadline 0 no permit was supplied
    if (permit.deadline != 0) {
      try
        IERC20WithPermit(depositToAave ? address(_aTokenUnderlying) : address(_aToken)).permit(
          depositor,
          address(this),
          permit.value,
          permit.deadline,
          permit.v,
          permit.r,
          permit.s
        )
      {} catch {}
    }
    (uint256 shares, ) = _deposit(depositor, receiver, 0, assets, referralCode, depositToAave);
    return shares;
  }

  ///@inheritdoc IStaticATokenLM
  function metaWithdraw(
    address owner,
    address receiver,
    uint256 shares,
    uint256 assets,
    bool withdrawFromAave,
    uint256 deadline,
    SignatureParams calldata sigParams
  ) external returns (uint256, uint256) {
    require(owner != address(0), StaticATokenErrors.INVALID_OWNER);
    //solium-disable-next-line
    require(deadline >= block.timestamp, StaticATokenErrors.INVALID_EXPIRATION);
    uint256 nonce = nonces[owner];
    // Unchecked because the only math done is incrementing
    // the owner's nonce which cannot realistically overflow.
    unchecked {
      bytes32 digest = keccak256(
        abi.encodePacked(
          "\x19\x01",
          DOMAIN_SEPARATOR(),
          keccak256(abi.encode(METAWITHDRAWAL_TYPEHASH, owner, receiver, shares, assets, withdrawFromAave, nonce, deadline))
        )
      );
      nonces[owner] = nonce + 1;
      require(owner == ECDSA.recover(digest, sigParams.v, sigParams.r, sigParams.s), StaticATokenErrors.INVALID_SIGNATURE);
    }
    return _withdraw(owner, receiver, shares, assets, withdrawFromAave);
  }

  ///@inheritdoc IERC4626
  function previewRedeem(uint256 shares) public view virtual returns (uint256) {
    return _convertToAssets(shares, Rounding.DOWN);
  }

  ///@inheritdoc IERC4626
  function previewMint(uint256 shares) public view virtual returns (uint256) {
    return _convertToAssets(shares, Rounding.UP);
  }

  ///@inheritdoc IERC4626
  function previewWithdraw(uint256 assets) public view virtual returns (uint256) {
    return _convertToShares(assets, Rounding.UP);
  }

  ///@inheritdoc IERC4626
  function previewDeposit(uint256 assets) public view virtual returns (uint256) {
    return _convertToShares(assets, Rounding.DOWN);
  }

  ///@inheritdoc IStaticATokenLM
  function rate() public view returns (uint256) {
    return POOL.getReserveNormalizedIncome(_aTokenUnderlying);
  }

  ///@inheritdoc IStaticATokenLM
  function collectAndUpdateRewards(address reward) public returns (uint256) {
    if (reward == address(0)) {
      return 0;
    }

    address[] memory assets = new address[](1);
    assets[0] = address(_aToken);

    return REWARDS_CONTROLLER.claimRewards(assets, type(uint256).max, address(this), reward);
  }

  ///@inheritdoc IStaticATokenLM
  function claimRewardsOnBehalf(address onBehalfOf, address receiver, address[] memory rewards) external {
    require(msg.sender == onBehalfOf || msg.sender == REWARDS_CONTROLLER.getClaimer(onBehalfOf), StaticATokenErrors.INVALID_CLAIMER);
    _claimRewardsOnBehalf(onBehalfOf, receiver, rewards);
  }

  ///@inheritdoc IStaticATokenLM
  function claimRewards(address receiver, address[] memory rewards) external {
    _claimRewardsOnBehalf(msg.sender, receiver, rewards);
  }

  ///@inheritdoc IStaticATokenLM
  function claimRewardsToSelf(address[] memory rewards) external {
    _claimRewardsOnBehalf(msg.sender, msg.sender, rewards);
  }

  ///@inheritdoc IStaticATokenLM
  function getCurrentRewardsIndex(address reward) public view returns (uint256) {
    if (address(reward) == address(0)) {
      return 0;
    }
    (, uint256 nextIndex) = REWARDS_CONTROLLER.getAssetIndex(address(_aToken), reward);
    return nextIndex;
  }

  ///@inheritdoc IStaticATokenLM
  function getTotalClaimableRewards(address reward) external view returns (uint256) {
    if (reward == address(0)) {
      return 0;
    }

    address[] memory assets = new address[](1);
    assets[0] = address(_aToken);
    uint256 freshRewards = REWARDS_CONTROLLER.getUserRewards(assets, address(this), reward);
    return IERC20(reward).balanceOf(address(this)) + freshRewards;
  }

  ///@inheritdoc IStaticATokenLM
  function getClaimableRewards(address user, address reward) external view returns (uint256) {
    return _getClaimableRewards(user, reward, balanceOf[user], getCurrentRewardsIndex(reward));
  }

  ///@inheritdoc IStaticATokenLM
  function getUnclaimedRewards(address user, address reward) external view returns (uint256) {
    return _userRewardsData[user][reward].unclaimedRewards;
  }

  ///@inheritdoc IERC4626
  function asset() external view returns (address) {
    return address(_aTokenUnderlying);
  }

  ///@inheritdoc IStaticATokenLM
  function aToken() external view returns (IERC20) {
    return _aToken;
  }

  ///@inheritdoc IStaticATokenLM
  function rewardTokens() external view returns (address[] memory) {
    return _rewardTokens;
  }

  ///@inheritdoc IERC4626
  function totalAssets() external view returns (uint256) {
    return _aToken.balanceOf(address(this));
  }

  ///@inheritdoc IERC4626
  function convertToShares(uint256 assets) external view returns (uint256) {
    return _convertToShares(assets, Rounding.DOWN);
  }

  ///@inheritdoc IERC4626
  function convertToAssets(uint256 shares) external view returns (uint256) {
    return _convertToAssets(shares, Rounding.DOWN);
  }

  ///@inheritdoc IERC4626
  function maxMint(address) public view virtual returns (uint256) {
    uint256 assets = maxDeposit(address(0));
    if (assets == type(uint256).max) return type(uint256).max;
    return _convertToShares(assets, Rounding.DOWN);
  }

  ///@inheritdoc IERC4626
  function maxWithdraw(address owner) public view virtual returns (uint256) {
    uint256 shares = maxRedeem(owner);
    return _convertToAssets(shares, Rounding.DOWN);
  }

  ///@inheritdoc IERC4626
  function maxRedeem(address owner) public view virtual returns (uint256) {
    address cachedATokenUnderlying = _aTokenUnderlying;
    DataTypes.ReserveData memory reserveData = POOL.getReserveData(cachedATokenUnderlying);

    // if paused or inactive users cannot withdraw underlying
    if (!ReserveConfiguration.getActive(reserveData.configuration) || ReserveConfiguration.getPaused(reserveData.configuration)) {
      return 0;
    }

    // otherwise users can withdraw up to the available amount
    uint256 underlyingTokenBalanceInShares = _convertToShares(
      IERC20(cachedATokenUnderlying).balanceOf(reserveData.aTokenAddress),
      Rounding.DOWN
    );
    uint256 cachedUserBalance = balanceOf[owner];
    return underlyingTokenBalanceInShares >= cachedUserBalance ? cachedUserBalance : underlyingTokenBalanceInShares;
  }

  ///@inheritdoc IERC4626
  function maxDeposit(address) public view virtual returns (uint256) {
    DataTypes.ReserveData memory reserveData = POOL.getReserveData(_aTokenUnderlying);

    // if inactive, paused or frozen users cannot deposit underlying
    if (
      !ReserveConfiguration.getActive(reserveData.configuration) ||
      ReserveConfiguration.getPaused(reserveData.configuration) ||
      ReserveConfiguration.getFrozen(reserveData.configuration)
    ) {
      return 0;
    }

    uint256 supplyCap = ReserveConfiguration.getSupplyCap(reserveData.configuration) *
      (10 ** ReserveConfiguration.getDecimals(reserveData.configuration));
    // if no supply cap deposit is unlimited
    if (supplyCap == 0) return type(uint256).max;
    // return remaining supply cap margin
    uint256 currentSupply = (IAToken(reserveData.aTokenAddress).scaledTotalSupply() + reserveData.accruedToTreasury).rayMulRoundUp(
      _getNormalizedIncome(reserveData)
    );
    return currentSupply > supplyCap ? 0 : supplyCap - currentSupply;
  }

  ///@inheritdoc IERC4626
  function deposit(uint256 assets, address receiver) external virtual returns (uint256) {
    (uint256 shares, ) = _deposit(msg.sender, receiver, 0, assets, 0, true);
    return shares;
  }

  ///@inheritdoc IERC4626
  function mint(uint256 shares, address receiver) external virtual returns (uint256) {
    (, uint256 assets) = _deposit(msg.sender, receiver, shares, 0, 0, true);

    return assets;
  }

  ///@inheritdoc IERC4626
  function withdraw(uint256 assets, address receiver, address owner) external virtual returns (uint256) {
    (uint256 shares, ) = _withdraw(owner, receiver, 0, assets, true);

    return shares;
  }

  ///@inheritdoc IERC4626
  function redeem(uint256 shares, address receiver, address owner) external virtual returns (uint256) {
    (, uint256 assets) = _withdraw(owner, receiver, shares, 0, true);

    return assets;
  }

  /// @notice Deposit aTokens and mint static tokens to receiver
  function depositATokens(uint256 aTokenAmount, address receiver) external override returns (uint256) {
    require(aTokenAmount > 0, StaticATokenErrors.INVALID_ZERO_AMOUNT);
    // allow compensation for rebase during tx
    uint256 userBalance = _aToken.balanceOf(msg.sender);
    uint256 amount = aTokenAmount > userBalance ? userBalance : aTokenAmount;
    // determine shares to mint
    uint256 shares = previewDeposit(amount);
    require(shares != 0, StaticATokenErrors.INVALID_ZERO_AMOUNT);
    // transfer aTokens in
    _aToken.safeTransferFrom(msg.sender, address(this), amount);
    // mint static tokens
    _mint(receiver, shares);
    emit Deposit(msg.sender, receiver, amount, shares);
    return shares;
  }

  /// @notice Burn static tokens and return aTokens to receiver
  function redeemATokens(uint256 shares, address receiver, address owner) external override returns (uint256) {
    require(shares > 0, StaticATokenErrors.INVALID_ZERO_AMOUNT);
    // determine assets to return
    uint256 assets = previewRedeem(shares);
    require(assets != 0, StaticATokenErrors.INVALID_ZERO_AMOUNT);
    // handle allowance if not owner
    if (msg.sender != owner) {
      uint256 allowed = allowance[owner][msg.sender];
      if (allowed != type(uint256).max) {
        allowance[owner][msg.sender] = allowed - shares;
      }
    }
    // burn static tokens
    _burn(owner, shares);
    emit Withdraw(msg.sender, receiver, owner, assets, shares);
    // transfer aTokens out
    _aToken.safeTransfer(receiver, assets);
    return assets;
  }

  function _deposit(
    address depositor,
    address receiver,
    uint256 _shares,
    uint256 _assets,
    uint16 referralCode,
    bool depositToAave
  ) internal returns (uint256, uint256) {
    require(receiver != address(0), StaticATokenErrors.INVALID_RECIPIENT);
    require(_shares == 0 || _assets == 0, StaticATokenErrors.ONLY_ONE_AMOUNT_FORMAT_ALLOWED);

    uint256 assets = _assets;
    uint256 shares = _shares;
    if (shares > 0) {
      if (depositToAave) {
        require(shares <= maxMint(receiver), "ERC4626: mint more than max");
      }
      assets = previewMint(shares);
    } else {
      if (depositToAave) {
        require(assets <= maxDeposit(receiver), "ERC4626: deposit more than max");
      }
      shares = previewDeposit(assets);
    }
    require(shares != 0, StaticATokenErrors.INVALID_ZERO_AMOUNT);

    if (depositToAave) {
      address cachedATokenUnderlying = _aTokenUnderlying;
      SafeERC20.safeTransferFrom(IERC20(cachedATokenUnderlying), depositor, address(this), assets);
      POOL.deposit(cachedATokenUnderlying, assets, address(this), referralCode);
    } else {
      _aToken.safeTransferFrom(depositor, address(this), assets);
    }

    _mint(receiver, shares);

    emit Deposit(depositor, receiver, assets, shares);

    return (shares, assets);
  }

  function _withdraw(
    address owner,
    address receiver,
    uint256 _shares,
    uint256 _assets,
    bool withdrawFromAave
  ) internal returns (uint256, uint256) {
    require(receiver != address(0), StaticATokenErrors.INVALID_RECIPIENT);
    require(_shares == 0 || _assets == 0, StaticATokenErrors.ONLY_ONE_AMOUNT_FORMAT_ALLOWED);
    require(_shares != _assets, StaticATokenErrors.INVALID_ZERO_AMOUNT);

    uint256 assets = _assets;
    uint256 shares = _shares;

    if (shares > 0) {
      if (withdrawFromAave) {
        require(shares <= maxRedeem(owner), "ERC4626: redeem more than max");
      }
      assets = previewRedeem(shares);
    } else {
      if (withdrawFromAave) {
        require(assets <= maxWithdraw(owner), "ERC4626: withdraw more than max");
      }
      shares = previewWithdraw(assets);
    }

    if (msg.sender != owner) {
      uint256 allowed = allowance[owner][msg.sender]; // Saves gas for limited approvals.

      if (allowed != type(uint256).max) allowance[owner][msg.sender] = allowed - shares;
    }

    _burn(owner, shares);

    emit Withdraw(msg.sender, receiver, owner, assets, shares);

    if (withdrawFromAave) {
      POOL.withdraw(_aTokenUnderlying, assets, receiver);
    } else {
      _aToken.safeTransfer(receiver, assets);
    }

    return (shares, assets);
  }

  /**
   * @notice Updates rewards for senders and receiver in a transfer (not updating rewards for address(0))
   * @param from The address of the sender of tokens
   * @param to The address of the receiver of tokens
   */
  function _beforeTokenTransfer(address from, address to, uint256) internal override {
    for (uint256 i = 0; i < _rewardTokens.length; i++) {
      address rewardToken = address(_rewardTokens[i]);
      uint256 rewardsIndex = getCurrentRewardsIndex(rewardToken);
      if (from != address(0)) {
        _updateUser(from, rewardsIndex, rewardToken);
      }
      if (to != address(0) && from != to) {
        _updateUser(to, rewardsIndex, rewardToken);
      }
    }
  }

  /**
   * @notice Adding the pending rewards to the unclaimed for specific user and updating user index
   * @param user The address of the user to update
   * @param currentRewardsIndex The current rewardIndex
   * @param rewardToken The address of the reward token
   */
  function _updateUser(address user, uint256 currentRewardsIndex, address rewardToken) internal {
    uint256 balance = balanceOf[user];
    if (balance > 0) {
      _userRewardsData[user][rewardToken].unclaimedRewards = _getClaimableRewards(user, rewardToken, balance, currentRewardsIndex)
        .toUint128();
    }
    _userRewardsData[user][rewardToken].rewardsIndexOnLastInteraction = currentRewardsIndex.toUint128();
  }

  /**
   * @notice Compute the pending in WAD. Pending is the amount to add (not yet unclaimed) rewards in WAD.
   * @param balance The balance of the user
   * @param rewardsIndexOnLastInteraction The index which was on the last interaction of the user
   * @param currentRewardsIndex The current rewards index in the system
   * @param assetUnit One unit of asset (10**decimals)
   * @return The amount of pending rewards in WAD
   */
  function _getPendingRewards(
    uint256 balance,
    uint256 rewardsIndexOnLastInteraction,
    uint256 currentRewardsIndex,
    uint256 assetUnit
  ) internal pure returns (uint256) {
    if (balance == 0) {
      return 0;
    }
    return (balance * (currentRewardsIndex - rewardsIndexOnLastInteraction)) / assetUnit;
  }

  /**
   * @notice Compute the claimable rewards for a user
   * @param user The address of the user
   * @param reward The address of the reward
   * @param balance The balance of the user in WAD
   * @param currentRewardsIndex The current rewards index
   * @return The total rewards that can be claimed by the user (if `fresh` flag true, after updating rewards)
   */
  function _getClaimableRewards(
    address user,
    address reward,
    uint256 balance,
    uint256 currentRewardsIndex
  ) internal view returns (uint256) {
    RewardIndexCache memory rewardsIndexCache = _startIndex[reward];
    require(rewardsIndexCache.isRegistered == true, StaticATokenErrors.REWARD_NOT_INITIALIZED);
    UserRewardsData memory currentUserRewardsData = _userRewardsData[user][reward];
    uint256 assetUnit = 10 ** decimals;
    return
      currentUserRewardsData.unclaimedRewards +
      _getPendingRewards(
        balance,
        currentUserRewardsData.rewardsIndexOnLastInteraction == 0
          ? rewardsIndexCache.lastUpdatedIndex
          : currentUserRewardsData.rewardsIndexOnLastInteraction,
        currentRewardsIndex,
        assetUnit
      );
  }

  /**
   * @notice Claim rewards on behalf of a user and send them to a receiver
   * @param onBehalfOf The address to claim on behalf of
   * @param rewards The addresses of the rewards
   * @param receiver The address to receive the rewards
   */
  function _claimRewardsOnBehalf(address onBehalfOf, address receiver, address[] memory rewards) internal {
    for (uint256 i = 0; i < rewards.length; i++) {
      if (address(rewards[i]) == address(0)) {
        continue;
      }
      uint256 currentRewardsIndex = getCurrentRewardsIndex(rewards[i]);
      uint256 balance = balanceOf[onBehalfOf];
      uint256 userReward = _getClaimableRewards(onBehalfOf, rewards[i], balance, currentRewardsIndex);
      uint256 totalRewardTokenBalance = IERC20(rewards[i]).balanceOf(address(this));
      uint256 unclaimedReward = 0;

      if (userReward > totalRewardTokenBalance) {
        totalRewardTokenBalance += collectAndUpdateRewards(address(rewards[i]));
      }

      if (userReward > totalRewardTokenBalance) {
        unclaimedReward = userReward - totalRewardTokenBalance;
        userReward = totalRewardTokenBalance;
      }
      if (userReward > 0) {
        _userRewardsData[onBehalfOf][rewards[i]].unclaimedRewards = unclaimedReward.toUint128();
        _userRewardsData[onBehalfOf][rewards[i]].rewardsIndexOnLastInteraction = currentRewardsIndex.toUint128();
        IERC20(rewards[i]).safeTransfer(receiver, userReward);
      }
    }
  }

  function _convertToShares(uint256 assets, Rounding rounding) internal view returns (uint256) {
    if (rounding == Rounding.UP) return assets.rayDivRoundUp(rate());
    return assets.rayDivRoundDown(rate());
  }

  function _convertToAssets(uint256 shares, Rounding rounding) internal view returns (uint256) {
    if (rounding == Rounding.UP) return shares.rayMulRoundUp(rate());
    return shares.rayMulRoundDown(rate());
  }

  /**
   * @notice Initializes a new rewardToken
   * @param reward The reward token to be registered
   */
  function _registerRewardToken(address reward) internal {
    if (isRegisteredRewardToken(reward)) return;
    uint256 startIndex = getCurrentRewardsIndex(reward);

    _rewardTokens.push(reward);
    _startIndex[reward] = RewardIndexCache(true, uint240(startIndex));

    emit RewardTokenRegistered(reward, startIndex);
  }

  /**
   * @notice Returns the ongoing normalized income for the reserve.
   * @dev A value of 1e27 means there is no income. As time passes, the income is accrued
   * @dev A value of 2*1e27 means for each unit of asset one unit of income has been accrued
   * @param reserve The reserve object
   * @return The normalized income, expressed in ray
   */
  function _getNormalizedIncome(DataTypes.ReserveData memory reserve) internal view returns (uint256) {
    uint40 timestamp = reserve.lastUpdateTimestamp;

    //solium-disable-next-line
    if (timestamp == block.timestamp) {
      //if the index was updated in the same block, no need to perform any calculation
      return reserve.liquidityIndex;
    } else {
      return MathUtils.calculateLinearInterest(reserve.currentLiquidityRate, timestamp).rayMul(reserve.liquidityIndex);
    }
  }
}

contracts/vaults/dstake/adapters/Wrapped.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IDStableConversionAdapter } from "../interfaces/IDStableConversionAdapter.sol";
import { IStaticATokenLM } from "../../atoken_wrapper/interfaces/IStaticATokenLM.sol"; // Interface for StaticATokenLM
import { IERC4626 } from "@openzeppelin/contracts/interfaces/IERC4626.sol";

/**
 * @title WrappedDLendConversionAdapter
 * @notice Adapter for converting between a dSTABLE asset (like dUSD) and a specific wrapped dLEND aToken
 *         (like wddUSD, implemented via StaticATokenLM). The wrapped dLEND token address must be provided at deployment.
 * @dev Implements the IDStableConversionAdapter interface.
 *      Interacts with a specific StaticATokenLM contract provided at deployment.
 */
contract WrappedDLendConversionAdapter is IDStableConversionAdapter {
  using SafeERC20 for IERC20;

  // --- Errors ---
  error ZeroAddress();
  error InvalidAmount();
  error InconsistentState(string message);

  // --- State ---
  address public immutable dStable; // The underlying dSTABLE asset (e.g., dUSD)
  IStaticATokenLM public immutable wrappedDLendToken; // The wrapped dLEND aToken (StaticATokenLM instance, e.g., wddUSD)
  address public immutable collateralVault; // The DStakeCollateralVault to deposit wrappedDLendToken into

  // --- Constructor ---
  /**
   * @param _dStable The address of the dSTABLE asset (e.g., dUSD)
   * @param _wrappedDLendToken The address of the wrapped dLEND token (StaticATokenLM, e.g., wddUSD)
   * @param _collateralVault The address of the DStakeCollateralVault
   */
  constructor(address _dStable, address _wrappedDLendToken, address _collateralVault) {
    if (_dStable == address(0) || _wrappedDLendToken == address(0) || _collateralVault == address(0)) {
      revert ZeroAddress();
    }
    dStable = _dStable;
    wrappedDLendToken = IStaticATokenLM(_wrappedDLendToken);
    collateralVault = _collateralVault;

    // Sanity check: Ensure the StaticATokenLM wrapper uses the correct underlying by casting to IERC4626
    if (IERC4626(_wrappedDLendToken).asset() != _dStable) {
      revert InconsistentState("StaticATokenLM underlying mismatch");
    }
  }

  // --- IDStableConversionAdapter Implementation ---

  /**
   * @inheritdoc IDStableConversionAdapter
   * @dev Converts dStable -> wrappedDLendToken by depositing into StaticATokenLM.
   *      The StaticATokenLM contract MUST be pre-approved to spend dStable held by this adapter.
   *      The StaticATokenLM contract mints the wrappedDLendToken directly to the collateralVault.
   */
  function convertToVaultAsset(uint256 dStableAmount) external override returns (address _vaultAsset, uint256 vaultAssetAmount) {
    if (dStableAmount == 0) {
      revert InvalidAmount();
    }

    // 1. Pull dStable from caller (Router)
    IERC20(dStable).safeTransferFrom(msg.sender, address(this), dStableAmount);

    // 2. Approve the StaticATokenLM wrapper to pull the dStable
    IERC20(dStable).forceApprove(address(wrappedDLendToken), dStableAmount);

    // 3. Deposit dStable into the StaticATokenLM wrapper, minting wrappedDLendToken to collateralVault
    vaultAssetAmount = IERC4626(address(wrappedDLendToken)).deposit(dStableAmount, collateralVault);

    return (address(wrappedDLendToken), vaultAssetAmount);
  }

  /**
   * @inheritdoc IDStableConversionAdapter
   * @dev Converts wrappedDLendToken -> dStable by withdrawing from StaticATokenLM.
   *      The StaticATokenLM contract sends the dStable directly to msg.sender.
   */
  function convertFromVaultAsset(uint256 vaultAssetAmount) external override returns (uint256 dStableAmount) {
    if (vaultAssetAmount == 0) {
      revert InvalidAmount();
    }

    // 1. Pull wrappedDLendToken (shares) from caller (Router)
    IERC20(address(wrappedDLendToken)).safeTransferFrom(msg.sender, address(this), vaultAssetAmount);

    // 2. Withdraw from StaticATokenLM, sending dStable to msg.sender
    dStableAmount = IERC4626(address(wrappedDLendToken)).redeem(vaultAssetAmount, msg.sender, address(this));

    if (dStableAmount == 0) {
      revert InvalidAmount();
    }

    return dStableAmount;
  }

  /**
   * @inheritdoc IDStableConversionAdapter
   * @dev Uses StaticATokenLM's previewRedeem function to get the underlying value (dStable).
   */
  function assetValueInDStable(address _vaultAsset, uint256 vaultAssetAmount) external view override returns (uint256 dStableValue) {
    if (_vaultAsset != address(wrappedDLendToken)) {
      revert InconsistentState("Incorrect vault asset address");
    }
    // previewRedeem takes shares (vaultAssetAmount) and returns assets (dStableValue)
    return IERC4626(address(wrappedDLendToken)).previewRedeem(vaultAssetAmount);
  }

  /**
   * @inheritdoc IDStableConversionAdapter
   */
  function vaultAsset() external view override returns (address) {
    return address(wrappedDLendToken);
  }

  /**
   * @inheritdoc IDStableConversionAdapter
   * @dev Preview the result of converting a given dSTABLE amount to wrappedDLendToken.
   * @param dStableAmount Amount of dSTABLE to convert
   * @return _vaultAsset Address of the vault asset (wrapped dLend token)
   * @return vaultAssetAmount Amount of vault asset that would be received
   */
  function previewConvertToVaultAsset(uint256 dStableAmount) public view override returns (address _vaultAsset, uint256 vaultAssetAmount) {
    _vaultAsset = address(wrappedDLendToken);
    vaultAssetAmount = IERC4626(address(wrappedDLendToken)).previewDeposit(dStableAmount);
  }

  /**
   * @inheritdoc IDStableConversionAdapter
   * @dev Preview the result of converting a given wrappedDLendToken amount to dSTABLE.
   * @param vaultAssetAmount Amount of vault asset to convert
   * @return dStableAmount Amount of dSTABLE that would be received
   */
  function previewConvertFromVaultAsset(uint256 vaultAssetAmount) public view override returns (uint256 dStableAmount) {
    dStableAmount = IERC4626(address(wrappedDLendToken)).previewRedeem(vaultAssetAmount);
  }
}

contracts/vaults/dstake/DStakeCollateral.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IERC20 } from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import { SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import { IDStakeCollateralVault } from "./interfaces/IDStakeCollateralVault.sol";
import { IDStableConversionAdapter } from "./interfaces/IDStableConversionAdapter.sol";
import { AccessControl } from "@openzeppelin/contracts/access/AccessControl.sol";
import { EnumerableSet } from "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import { ReentrancyGuard } from "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

// ---------------------------------------------------------------------------
// Internal interface to query the router's public mapping without importing the
// full router contract (avoids circular dependencies).
// ---------------------------------------------------------------------------
interface IAdapterProvider {
  function vaultAssetToAdapter(address) external view returns (address);
}

/**
 * @title DStakeCollateralVault
 * @notice Holds various yield-bearing/convertible ERC20 tokens (`vault assets`) managed by dSTAKE.
 * @dev Calculates the total value of these assets in terms of the underlying dStable asset
 *      using registered adapters. This contract is non-upgradeable but replaceable via
 *      DStakeToken governance.
 *      Uses AccessControl for role-based access control.
 */
contract DStakeCollateralVault is IDStakeCollateralVault, AccessControl, ReentrancyGuard {
  using SafeERC20 for IERC20;
  using EnumerableSet for EnumerableSet.AddressSet;

  // --- Roles ---
  bytes32 public constant ROUTER_ROLE = keccak256("ROUTER_ROLE");

  // --- Errors ---
  error ZeroAddress();
  error AssetNotSupported(address asset);
  error AssetAlreadySupported(address asset);
  error NonZeroBalance(address asset);
  error CannotRescueRestrictedToken(address token);
  error ETHTransferFailed(address receiver, uint256 amount);

  // --- Events ---
  event TokenRescued(address indexed token, address indexed receiver, uint256 amount);
  event ETHRescued(address indexed receiver, uint256 amount);

  // --- State ---
  address public immutable dStakeToken; // The DStakeToken this vault serves
  address public immutable dStable; // The underlying dStable asset address

  address public router; // The DStakeRouter allowed to interact

  EnumerableSet.AddressSet private _supportedAssets; // Set of supported vault assets

  // --- Constructor ---
  constructor(address _dStakeVaultShare, address _dStableAsset) {
    if (_dStakeVaultShare == address(0) || _dStableAsset == address(0)) {
      revert ZeroAddress();
    }
    dStakeToken = _dStakeVaultShare;
    dStable = _dStableAsset;

    // Set up the DEFAULT_ADMIN_ROLE initially to the contract deployer
    _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
  }

  // --- External Views (IDStakeCollateralVault Interface) ---

  /**
   * @inheritdoc IDStakeCollateralVault
   */
  function totalValueInDStable() external view override returns (uint256 dStableValue) {
    uint256 totalValue = 0;
    uint256 len = _supportedAssets.length();
    for (uint256 i = 0; i < len; i++) {
      address vaultAsset = _supportedAssets.at(i);
      address adapterAddress = IAdapterProvider(router).vaultAssetToAdapter(vaultAsset);

      if (adapterAddress == address(0)) {
        // If there is no adapter configured, simply skip this asset to
        // preserve liveness. Anyone can dust this vault and we cannot
        // enforce that all assets have adapters before removal
        continue;
      }

      uint256 balance = IERC20(vaultAsset).balanceOf(address(this));
      if (balance > 0) {
        totalValue += IDStableConversionAdapter(adapterAddress).assetValueInDStable(vaultAsset, balance);
      }
    }
    return totalValue;
  }

  // --- External Functions (Router Interactions) ---

  /**
   * @notice Transfers `amount` of `vaultAsset` from this vault to `recipient`.
   * @dev Only callable by the registered router (ROUTER_ROLE).
   * @param vaultAsset The vault asset to transfer
   * @param amount Amount of tokens to transfer
   * @param recipient Address to receive the tokens
   */
  function sendAsset(address vaultAsset, uint256 amount, address recipient) external onlyRole(ROUTER_ROLE) {
    if (!_isSupported(vaultAsset)) revert AssetNotSupported(vaultAsset);
    IERC20(vaultAsset).safeTransfer(recipient, amount);
  }

  /**
   * @notice Adds a new supported vault asset. Can only be invoked by the router.
   * @dev Only callable by the registered router (ROUTER_ROLE).
   * @param vaultAsset Address of the vault asset to add
   */
  function addSupportedAsset(address vaultAsset) external onlyRole(ROUTER_ROLE) {
    if (vaultAsset == address(0)) revert ZeroAddress();
    if (_isSupported(vaultAsset)) revert AssetAlreadySupported(vaultAsset);

    _supportedAssets.add(vaultAsset);
    emit SupportedAssetAdded(vaultAsset);
  }

  /**
   * @notice Removes a supported vault asset. Can only be invoked by the router.
   * @dev Only callable by the registered router (ROUTER_ROLE).
   * @param vaultAsset Address of the vault asset to remove
   */
  function removeSupportedAsset(address vaultAsset) external onlyRole(ROUTER_ROLE) {
    if (!_isSupported(vaultAsset)) revert AssetNotSupported(vaultAsset);
    // NOTE: Previously this function reverted if the vault still held a
    // non-zero balance of the asset, causing a griefing / DoS vector:
    // anyone could deposit 1 wei of the token to block removal. The
    // check has been removed so governance can always delist an asset.

    _supportedAssets.remove(vaultAsset);
    emit SupportedAssetRemoved(vaultAsset);
  }

  // --- Governance Functions ---

  /**
   * @notice Sets the router address. Grants ROUTER_ROLE to new router and
   *         revokes it from the previous router.
   * @dev Only callable by DEFAULT_ADMIN_ROLE.
   * @param _newRouter Address of the new router
   */
  function setRouter(address _newRouter) external onlyRole(DEFAULT_ADMIN_ROLE) {
    if (_newRouter == address(0)) revert ZeroAddress();

    // Revoke role from old router
    if (router != address(0)) {
      _revokeRole(ROUTER_ROLE, router);
    }

    _grantRole(ROUTER_ROLE, _newRouter);
    router = _newRouter;
    emit RouterSet(_newRouter);
  }

  // --- Internal Utilities ---

  function _isSupported(address asset) private view returns (bool) {
    return _supportedAssets.contains(asset);
  }

  // --- External Views ---

  /**
   * @notice Returns the vault asset at `index` from the internal supported set.
   *         Kept for backwards-compatibility with the previous public array getter.
   */
  function supportedAssets(uint256 index) external view override returns (address) {
    return _supportedAssets.at(index);
  }

  /**
   * @notice Returns the entire list of supported vault assets. Useful for UIs & off-chain tooling.
   */
  function getSupportedAssets() external view returns (address[] memory) {
    return _supportedAssets.values();
  }

  // --- Recovery Functions ---

  /**
   * @notice Rescues tokens accidentally sent to the contract
   * @dev Cannot rescue supported vault assets or the dStable token
   * @param token Address of the token to rescue
   * @param receiver Address to receive the rescued tokens
   * @param amount Amount of tokens to rescue
   */
  function rescueToken(address token, address receiver, uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant {
    if (receiver == address(0)) revert ZeroAddress();

    // Check if token is a supported asset
    if (_isSupported(token)) {
      revert CannotRescueRestrictedToken(token);
    }

    // Check if token is the dStable token
    if (token == dStable) {
      revert CannotRescueRestrictedToken(token);
    }

    // Rescue the token
    IERC20(token).safeTransfer(receiver, amount);
    emit TokenRescued(token, receiver, amount);
  }

  /**
   * @notice Rescues ETH accidentally sent to the contract
   * @param receiver Address to receive the rescued ETH
   * @param amount Amount of ETH to rescue
   */
  function rescueETH(address receiver, uint256 amount) external onlyRole(DEFAULT_ADMIN_ROLE) nonReentrant {
    if (receiver == address(0)) revert ZeroAddress();

    (bool success, ) = receiver.call{ value: amount }("");
    if (!success) revert ETHTransferFailed(receiver, amount);

    emit ETHRescued(receiver, amount);
  }

  /**
   * @notice Returns the list of tokens that cannot be rescued
   * @return restrictedTokens Array of restricted token addresses
   */
  function getRestrictedRescueTokens() external view returns (address[] memory) {
    address[] memory assets = _supportedAssets.values();
    address[] memory restrictedTokens = new address[](assets.length + 1);

    // Add all supported assets
    for (uint256 i = 0; i < assets.length; i++) {
      restrictedTokens[i] = assets[i];
    }

    // Add dStable token
    restrictedTokens[assets.length] = dStable;

    return restrictedTokens;
  }

  /**
   * @notice Allows the contract to receive ETH
   */
  receive() external payable {}
}

